import React from 'react'
import Slider from 'infinite-react-carousel';
import { CssBaseline, Grid, Box, Typography, Button } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles'; 
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';


const slides = [
  {name: "Carousel_1", 
   header: "Find your communities.", 
   detail: 
            `Discover family and mentors in 
            areas where you thought you were alone. 
            Discover individuals who share your 
            struggles or your quirkiest combination of interests.
            `
  },

  {name: "Carousel_2", 
   header: "Expand your horizons.", 
   detail: 
            `
            Encounter people and stories 
            that are vastly different from 
            you but still somehow similar. 
            Learn about new areas and how 
            they connect with your existing passions.
            `
  },

  {name: "Carousel_3", 
   header: "Coalition Building.", 
   detail: 
            `
            Easily find, communicate, 
            and organize with others who are 
            working towards similar causes.

            `
  },
]

const arrowstyles = {
    color: '#FF8F8F',
    '&:hover': {
        color: 'rgba(97, 94, 115,0.7)',
    },
    display: 'flex',
}

const CustomPrevArrow = withStyles((theme) => ({
  root: {
    arrowstyles 
  },
}))(ArrowBackIosIcon);

const CustomNextArrow = withStyles((theme) => ({
  root: {
    arrowstyles
  },
}))(ArrowForwardIosIcon);



const useStyles = makeStyles((theme) => ({
  root: {
    fontFamily: 'Poppins',
    textAlign: 'center',
    justifyContent: 'center', alignItems: 'center',
  },

  header: {
    fontSize: '3rem',
    color: 'black',
    fontWeight: 'bold',
    lineHeight: '99%', 
    marginBottom: '30px',
  },

  imgTextWrapper: {
    position: 'relative',
    width: 'calc(20rem + 10vw)',
    marginLeft: 'auto',
    marginRight: 'auto',
    justifyContent: 'center', alignItems: 'center',
    color: 'transparent',
    borderRadius: '30px',
    overflow: 'hidden',
    display: 'flex', 
  },

  screenshot: {
    width: 'calc(20rem + 10vw)',
  },  

  textBox: {
    color: 'transparent',
    fontSize: '1.7rem',
    fontWeight: 'bold',
    top: '0', left: '0', right: '0', bottom: '0', 
    position: 'absolute',

    '&:hover': {
      color: 'white',
      background: 'rgba(97, 94, 115,0.7)',
    },
  },

  detail: {
    height: 'fit-content', 
    padding: '20px',
    top: '0', left: '0', right: '0', bottom: '0', 
    position: 'absolute',
    marginTop: 'auto', marginBottom: 'auto',


  },
})); 


export default function Carousel() {

  const classes = useStyles();
  /*const [state, setState] = React.useState({
    hover: false
  });

  const toggleHover = () => {
    console.log('mouse enter/leave') 
    setState({
       hover: !state.hover
     });
  };
  */

  return (
    <div className={classes.root}>
      <Slider 
      dots
      slidesToShow={1}
      autoplay={true}
      autoplaySpeed={10000}
      autoplayScroll={1}
      prevArrow={<CustomPrevArrow/>}
      nextArrow={<CustomNextArrow/>}

    >
      {slides.map (slide => {
          return (
            <div>
                <Grid container item 
                  style={{}}
                >
                  <Grid item xs={12}>
                    <Box>
                        <Typography className={classes.header}>
                            {slide.header}
                        </Typography>
                    </Box>
                  </Grid>

                  <Grid item xs={12} 

                      style={{}}
                  >       
                          <Box className={classes.imgTextWrapper}
                            //onMouseEnter={() => toggleHover()} 
                            //onMouseLeave={() => toggleHover()}
                            //style={state.hover ? {background: 'rgba(97, 94, 115,0.7)'} : {background: 'transparent'}}
                          >
                            <div className={classes.imgWrapper}>
                              <img src={`${process.env.PUBLIC_URL + '/assets/' + slide.name + '.svg'}`} 
                              className={classes.screenshot}

                              />
                            </div>
                              <div className={classes.textBox} >
                                <span className={classes.detail} > {slide.detail}  </span>


                              </div>


                          </Box>
                  </Grid>

                </Grid>
            </div>
      )})}    
    </Slider>
    </div>
    ) 
}
