import React from 'react'
import Slider from 'infinite-react-carousel';
import { CssBaseline, Grid, Box, Typography, Button } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles'; 


const slides = [
  {"name": "Carousel_1", 
   "header": "Find your communities.", 
   "detail": 
            `Discover family and mentors in 
            areas where you thought you were alone. 
            Discover individuals who share your 
            struggles or your quirkiest combination of interests.
            `
  },

  {"name": "Carousel_2", 
   "header": "Expand your horizons.", 
   "detail": 
            `
            Encounter people and stories 
            that are vastly different from 
            you but still somehow similar. 
            Learn about new areas and how 
            they connect with your existing passions.
            `
  },

  {"name": "Carousel_3", 
   "header": "Coalition Building.", 
   "detail": 
            `
            Easily find, communicate, 
            and organize with others who are 
            working towards similar causes.

            `
  },
]

const useStyles = makeStyles((theme) => ({
  root: {
    fontFamily: 'Poppins',
    textAlign: 'center',
    justifyContent: 'center', alignItems: 'center',
  },

  header: {
    fontSize: '3rem',
    color: 'black',
    fontWeight: 'bold',
    lineHeight: '99%', 
    padding: '2px',
  },

  imgTextWrapper: {
    position: 'relative',
    width: 'calc(30rem + 10vw)',
    marginLeft: 'auto',
    marginRight: 'auto',
    justifyContent: 'center', alignItems: 'center',
    borderRadius: '100px',

  },

  screenshot: {
    // transform: 'rotate(-30deg)',
    width: 'calc(30rem + 10vw)',

  },  

  detail: {
    position: 'absolute',
    color: 'black',
    fontSize: '2.2rem',
    fontWeight: 'bold',
    top: '0', left: '0', right: '0', bottom: '0', 
    height: 'fit-content', 
    padding: '20px',
    marginTop: 'auto',
    marginBottom: 'auto',
  },
})); 


export default function Carousel() {

  const classes = useStyles();
  const [state, setState] = React.useState({
    hover: false
  });

  const toggleHover = () => {
     setState({
       hover: !state.hover
     });
  };

  return (
    <div className={classes.root}>
      <Slider 
      dots
      slidesToShow={1}
      autoplay={true}
      autoplaySpeed={10000}
      autoplayScroll={1}

    >
      {slides.map (slide => {
          return (
            <div>
                <Grid container item 
                  style={{}}
                >
                  <Grid item xs={12}>
                    <Box>
                        <Typography className={classes.header}>
                            {slide.header}
                        </Typography>
                    </Box>
                  </Grid>

                  <Grid item xs={12} 
                      onMouseEnter={() => toggleHover()} 
                      onMouseLeave={() => toggleHover()}
                      style={{}}
                  >
                    <Box>
                        <Typography>
                          <div className={classes.imgTextWrapper}>
                            <div className={classes.imgWrapper}>
                              <img src={`${process.env.PUBLIC_URL + '/assets/' + slide.name + '.svg'}`} 
                              className={classes.screenshot}/>
                            </div>
                            <div className={classes.detail}> {slide.detail} </div>

                          </div>
                        </Typography>
                    </Box>
                  </Grid>

                </Grid>
            </div>
      )})}    
    </Slider>
    </div>
    ) 
}
