import React, { useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Collapse, IconButton, Toolbar, Button, Grid, Divider, Box} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100vh',
        width: '100vw',
    }, 
    fire: {
        maxWidth: '400px',
        width: '100%',
    },
    title: {
        margin: '-3rem',
        fontSize: '10vw',
        color: 'black',
        textShadow: '4px 4px rgba(0, 0, 0, 0.3)',
        lineHeight: '99%', 
    },

})) 

export default function HeaderMainContent() {
    const classes = useStyles(); 

    return(
        <div className={classes.root}>
            <Grid
                container
                spacing={2}
                justify="center"
                alignItems="center"
            >
                 <Grid item xs={5}>
                    <div>
                        <img src={`${process.env.PUBLIC_URL + '/assets/fire.png'}`}  className={classes.fire}></img>
                    </div>             
                </Grid>

                <Grid item xs={4}>
                    {/*<Collapse in={checked} {...(checked ? { timeout: 1000 } : {})} collapsedHeight={50}> */}
                        <Grid container spacing={2}
                justify="center"
                alignItems="center">
                            <Grid item xs={12}>
                                <h1 className={classes.title}>campfire <br/> convos </h1>
                            </Grid>

                            <Grid item xs={12}>
                                <p classname={classes.subtitle}> an online platform reimagining <span style={{color: '#ff9999'}}> campus communities. </span></p> 
                            </Grid>

                        </Grid>

                    {/*</Collapse> */}
                </Grid>

            </Grid>
        </div>
    )

}
