import React, { useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Collapse, IconButton, Toolbar } from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { Link as Scroll} from 'react-scroll'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100vh',
        fontFamily: 'Alef',
    },
    appbar: {
        background: 'none',    
    },
    appbarWrapper: {
        // pushes bar towards the center
        width: '80%',
        margin: '0 auto', 
    },
    appbarTitle: {
        // separates hamburger from navbar text
        flexGrow: '1',
    },

    icon: {
        color: '#fff', 
        fontSize: '2rem',
    },
    colorText: {
        color: '#ff9f87',
    },
    container: {
        textAlign: 'center', 
    },
    title: {
        color: '#fff', 
        fontSize: '4rem',
        textShadowOffset: {width: 4, height: 6,},
        textShadowColor: 'black',
        textShadowOpacity: 0.25,
    },  
    goDown: {
        color: '#ff9f87', 
        fontSize: '4rem',
    },
})) 

export default function Header() {
    const classes = useStyles(); 
    // dont understand this code
    const [checked,setChecked] = useState(false);  
    useEffect(()=> {
        setChecked(true)
    }, [])
    return(
        <div className={classes.root} id='header'>
            <AppBar className = {classes.appbar} elevation={0}>
                <Toolbar className={classes.appbarWrapper}>
                    <h1 className={classes.appbarTitle}>
                        Campfire<span className={classes.colorText}>Conversations.</span> 
                    </h1>
                    <IconButton>
                        <SortIcon className={classes.icon}/>
                    </IconButton>
                </Toolbar>  
            </AppBar>

            <Collapse in={checked} {...(checked ? { timeout: 1000 } : {})} collapsedHeight={50}> 
                <div className={classes.container}> 
                    <h1 className={classes.title}>campfire</h1>
                    <h1 className={classes.title}>convos</h1>
                    <Scroll to="place-to-visit" smooth={true}>
                    <IconButton>
                        <ExpandMoreIcon className={classes.goDown}/>
                    </IconButton>
                    </Scroll>
                </div>
            </Collapse>

        </div>
    )
}
