import React, { useEffect, useState} from 'react';
import { createMuiTheme, makeStyles, withStyles } from '@material-ui/core/styles';
import { AppBar, Collapse, IconButton, Typography, Toolbar, Button, Grid, Divider, Box} from '@material-ui/core';
import schools from '../../static/schoolsOnboard.json' ;
import { green, purple, pink} from '@material-ui/core/colors';

/* not sure if we should have this or just hard code the number of schools 
-- this could unnecessarily slow up the page  */ 
const campusCount = Object.keys(schools).length

// register now button 
const ColorButton = withStyles((theme) => ({
    root: {
      color: theme.palette.getContrastText('#ff9999'),
      backgroundColor: '#ff9999',
      textTransform: 'none', 
      '&:hover': {
        backgroundColor: purple[700],
      },
    },
  }))(Button);

  const theme = createMuiTheme({
        palette: {
          primary: {
            main: '#ff9999',
          },
          secondary: pink,
        },

  });

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100vh',
        width: '100vw',
    }, 
    fire: {
        maxWidth: '400px',
        width: '100%',
    },
    text: {
        marginLeft: '-50px%'
    },
    title: {
        fontSize: '9vw',
        color: 'black',
        textShadow: '4px 4px rgba(0, 0, 0, 0.3)',
        lineHeight: '99%', 
    },

    subtitle: {
        marginTop: '-15%',
        fontSize: '3vw',
    },
    joinNow: {
        marginTop: '-30px',
        background: '#ff9999 30%'
    },
    registerButton: {
        textTransform: 'none', 
        root: {
            background: 'rgba(0,0,0,1)'
        },

    }


})) 

export default function HeaderMainContent() {
    const classes = useStyles(); 

    return(
        <div className={classes.root}>
            <Grid
                container
                spacing={2}
                justify="center"
                alignItems="center"
            >
                 <Grid item xs={4}>
                    <div>
                        <img src={`${process.env.PUBLIC_URL + '/assets/fire.png'}`}  className={classes.fire}></img>
                    </div>             
                </Grid>

                <Grid item xs={6} className={classes.text}>
                    {/*<Collapse in={checked} {...(checked ? { timeout: 1000 } : {})} collapsedHeight={50}> */}
                        <Grid container 
                            spacing={2}
                            justify='flex-start'
                            alignItems="center"

                        >
                            <Grid item xs={12}>
                            <Typography noWrap><h1 className={classes.title}>campfire <br/> convos </h1></Typography>

                            </Grid>

                            <Grid item xs={12}>

                                <p className={classes.subtitle}> an online platform reimagining <br/> <b style={{color: '#ff9999'}}> campus communities. </b></p> 
                            </Grid>

                            <Grid item xs={6}>
                                <Box display='flex' className={classes.joinNow}> 
                                    <Grid container direction='column' justify='center' alignItems="center">
                                        <Grid item xs={6} style={{textAlign: 'center'}}>
                                            <Box> <Typography> active on {campusCount} schools and counting. join today </Typography>  </Box>
                                        </Grid>
                                        <Grid item xs={4}><ColorButton style={{}}> register now </ColorButton> </Grid>
                                    </Grid>

                                </Box>
                            </Grid>

                            <Grid item xs={4}>
                                <Box display='flex'>
                                    Learn More
                                </Box>

                            </Grid>

                        </Grid>

                    {/*</Collapse> */}
                </Grid>

            </Grid>
        </div>
    )

}
