import React from 'react'
import { makeStyles } from '@material-ui/core/styles'; 
import { CssBaseline, Grid, Box } from '@material-ui/core';
import landingPageTags from "../../static/landingPageTags";
import TagArray from '../TagArray';


const useStyles = makeStyles((theme) => ({
    root: {
        // minHeight: '100vh', 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'left', 
        textAlign: 'left',
        paddingLeft: '50px',
        textAlign: 'center',
        color: 'black',
        //marginBottom: '-5rem',   
    },

    container: {
        maxWidth: '1000px',
        marginLeft: 'auto',
        marginRight: 'auto',

    },

    title: {
        fontFamily: 'Poppins',
        fontSize: '2rem',
        color: 'rgb(0,0,0,1)',
        fontWeight: 'bold',
        color: 'rgb(255, 143, 143,1)',
        lineHeight: '99%', 
        padding: '15px',
        lineHeight: .9,
        },
    section: {
        height: '100%',
    },

    box: {
        maxWidth: '100%',
        width: '100px',
        height: '100%',
        backgroundColor: 'pink',
        borderRadius: '10px', 
        padding: '20px',
    }
}))

export default function() {
    const classes = useStyles();  
    const chipStyle = {size: 'medium', border: '#000', variant: 'outlined'}
    return (
        <div className={classes.root} id="about">
            <Grid
            container
            spacing={6}
            justifyContent="center"
            direction='row'
            alignItems='stretch'
            className={classes.container}
            >


                <Grid item className={classes.section} xs={12}>
                    <h1 className={classes.title}>so, what do you want to connect about?</h1>
                    <TagArray tags={landingPageTags} chipStyle={chipStyle} />
                </Grid>
            </Grid>
        </div>

    )
}
