import React from 'react'
import Slider from 'infinite-react-carousel';
import { CssBaseline, Grid, Box, Typography, Button } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles'; 


const slides = [
  {"name": "Carousel_1", 
   "header": "Find your home.", 
   "detail": 
            `Discover family and mentors in 
            areas where you thought you were alone. 
            Discover individuals who share your 
            struggles or your quirkiest combination of interests.
            `
  },

  {"name": "Carousel_2", 
   "header": "Expand your horizons.", 
   "detail": 
            `
            Encounter people and stories 
            that are vastly different from 
            you but still somehow similar. 
            Learn about new areas and how 
            they connect with your existing passions.
            `
  },

  {"name": "Carousel_3", 
   "header": "Coalition Building.", 
   "detail": 
            `
            Easily find, communicate, 
            and organize with others who are 
            working towards similar causes.

            `
  },
]

const useStyles = makeStyles((theme) => ({
  screenshot: {
    // transform: 'rotate(-30deg)',
    width: 'calc(30rem + 10vw)',
    height: 'auto',
    padding: '5px',
    alignContent: 'center',
  },  

  header: {
    fontFamily: 'Poppins',
    fontSize: '1.5rem',
    color: 'black',
    fontWeight: 'bold',
    lineHeight: '99%', 
    padding: '2px',
  },
})); 


export default function Carousel() {

  const classes = useStyles();
  const [state, setState] = React.useState({
    hover: false
  });

  const toggleHover = () => {

  };

  return (
      <Slider 
      dots
      slidesToShow={1}
      autoplay={true}
      autoplaySpeed={3000}
      autoplayScroll={1}

    >
      {slides.map (slide => {
          return (
            <div>
                <Grid container item 
                  style={{}}
                  onMouseEnter={toggleHover()} onMouseLeave={toggleHover()}
                >
                  <Grid item xs={12}>
                    <Box>
                        <Typography className={classes.header}>
                            {slide.header}
                        </Typography>
                    </Box>
                  </Grid>

                  <Grid item xs={12}>
                    <Box>
                        <Typography>
                            <img src={`${process.env.PUBLIC_URL + '/assets/' + slide.name + '.svg'}`} ></img>
                        </Typography>
                    </Box>
                  </Grid>

                </Grid>
            </div>
      )})}    
    </Slider>
    ) 
}
