import React, { useEffect, useState, useRef} from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { AppBar, Collapse, IconButton, Toolbar, Button, Grid} from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
//import { Link } from 'react-router-dom';
import { Link as Scroll} from 'react-scroll'
import { Link } from 'react-router-dom';
import ThemedLink from './ThemedLink';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100vh',
        fontFamily: 'Alef',
        width: '100vw',
    },
    appbar: {
        background: 'rgba(254, 108, 108, 0.5)',    
        position: 'fixed',
        //height: 50,
        display: 'flex',

    },
    appbarWrapper: {
        width: '90%',
        margin: '0 auto',    
        borderRadius: 16,
    },
    appbarLogo: {
        flexGrow: 5,
        margin: '0 auto', 
    },
    drawer: {
        [theme.breakpoints.up('sm')]: {
          width: drawerWidth,
          flexShrink: 0,
        },
      },
    icon: {
        color: '#fff', 
        margin: '0 auto', 
        [theme.breakpoints.up('sm')]: {
            display: 'none',
        },
    },
    title: {
        margin: '-3rem',
        fontSize: '6rem',
        color: 'white',
    },
    colorText: {
        color: '#ff9f87',
    },
    toolBarButtons: {
        color: '#ffffff',
        fontSize: '1rem',
        [theme.breakpoints.down('sm')]: {
            display: 'none',
        }
    },
    containerRight: {
        textAlign: 'right',
        width: '40%'
    },  
    goDown: {
        color: '#ff9f87', 
        fontSize: '4rem',
    },
})) 

export default function ResponsiveNavBar(props) {

    const toolBarItemsDict = { 
        "about": "About", 
        "learnMore": "Learn More", 
        "ourTeam": "Our Team", 
    };
    var toolBarItems = [["about", "About"], ["learnMore", "Learn More"], ["ourTeam", "Our Team"], ]

    const classes = useStyles(); 
    const { window } = props;
    const theme = useTheme();
    const [mobileOpen, setMobileOpen] = React.useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
      };

    const drawer = (

        <div className={classes.toolBarButtons}>
            {toolBarItems.map(toolBarItem => {
                <Scroll to={toolBarItem[0]} smooth={true}>
                    <Button className={classes.toolBarButtons} style={{textTransform: 'none'}}>
                        {toolBarItem[1]}
                    </Button>
                </Scroll>
            })}   
            <Button 
                className={classes.toolBarButtons} 
                style={{textTransform: 'none'}}
                onClick={(e) => {
                    e.preventDefault();
                    window.location.href="https://campfireconvos.com/registration";
                }}
            >
                Register
            </Button>         
        </div>
    )

    const container = window !== undefined ? () => window().document.body : undefined;

    return(
        <div className={classes.root}>
            <AppBar className={classes.appbar} elevation={0}>
                <Toolbar className={classes.appbarWrapper}>
                    <Scroll to="header" smooth={true} className={classes.appbarLogo}>
                        <h1>
                            <Button>
                                <img src={`${process.env.PUBLIC_URL + '/assets/flamelogo.png'}`} width='35' height='35'></img>
                            </Button>
                        </h1>
                    </Scroll>

                    <IconButton
                        onClick={handleDrawerToggle}
                        className={classes.icon}
                    >
                        <SortIcon />
                    </IconButton>
                </Toolbar>  
            </AppBar>
            <nav className={classes.drawer} >
                <Hidden smUp implementation="css">
                    <Drawer
                        container={container}
                        variant="temporary"
                        anchor={theme.direction='top'}
                        open={mobileOpen}
                        onClose={handleDrawerToggle}
                        classes={{
                            paper: classes.drawerPaper,
                        }}
                        ModalProps={{
                            keepMounted: true, // Better open performace on mobile.
                        }}
                        >
                            {drawer}
                        </Drawer>
                </Hidden>
                <Hidden xsDown implementation="css">
                    <Drawer
                        classes={{
                            paper: classes.drawerPaper,
                        }}
                        variant="permanent"
                        open
                    >
                        {drawer}
                    </Drawer>
                </Hidden>
            </nav>
        </div>

    )
}
