import React from 'react'
import { makeStyles } from '@material-ui/core/styles'; 
import { MovieFilter } from '@material-ui/icons';
import { CssBaseline, Grid } from '@material-ui/core';
import useWindowPosition from '../hook/useWindowPosition';
import teamMembers from "../static/teamMembers";
import TeamMemberCard from './TeamMemberCard';
import TagArray from './TagArray';
import teamMemberTags from "../static/teamMemberTags";

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh', 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        flexWrap: 'wrap',
        marginLeft: 'auto',
        marginRight: 'auto',
    },
    titleWrapper: {
        margin: '3rem',
        textAlign: 'center',
    },

    title: {
        fontFamily: 'Poppins',
        fontSize: '6rem',
        margine: '-1rem',
        color: 'white',
    },
    card: {
        height: '100%',
    }
}))


export default function() {
    const classes = useStyles();  
    const checked = useWindowPosition('aboutTags');
    return (

        <div className={classes.root} id="ourTeam">
            <Grid 
                container
                spacing={2}
                justify="center"
                alignItems="stretch"
                alignContent="stretch"
            >
                <Grid item xs={12}>
                <div className={classes.titleWrapper}>
                    <h1 className={classes.title}>our team</h1>
                </div>
                </Grid>

                {
                    teamMembers.map(member => {
                        return (
                            <Grid item md={2} sm={4} xs={12} className={classes.card}>
                            <TeamMemberCard member={member} checked={checked}/>
                            </Grid>
                        )
                    })
                }
            </Grid>

        </div>
    )
}
