import React from 'react'
import Slider from 'infinite-react-carousel';
import { CssBaseline, Grid, Box, Typography, Button } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles'; 



const useStyles = makeStyles((theme) => ({
  screenshot: {
    // transform: 'rotate(-30deg)',
    width: 'calc(30rem + 10vw)',
    height: 'auto',
    padding: '5px',
    alignContent: 'center',
  },  
})); 


const slides = [
  {"name": "Carousel_1", 
   "header": "Find your home.", 
   "detail": 
      `Discover family and mentors in 
      areas where you thought you were alone. 
      Discover individuals who share your 
      struggles or your quirkiest combination of interests.
      `
  },
  {"name": "Carousel_2", 
   "header": -71.116943, 
   "detail": 42.374443}, 
  {"name": "Carousel_3",
  "header": -71.116943, 
  "detail": 42.374443}, 
]



export default class Carousel extends React.Component {

  state = {
    open: false,
    hovered: true
    };


  render () {
  const classes = useStyles();  

    return (
      <Slider 
        dots
        slidesToShow={1}
        autoplay={true}
        autoplaySpeed={3000}
        autoplayScroll={1}

      >
        <div>
          <Grid item xs={12}>
            <Box>
                <Typography>
                    <img src={`${process.env.PUBLIC_URL + '/assets/Carousel_1.svg'}`} className={classes.screenshot} ></img>
                </Typography>
            </Box>
          </Grid>
        </div>
        <div>
          <Grid item xs={12}>
            <Box>
                <Typography>
                    <img src={`${process.env.PUBLIC_URL + '/assets/Carousel_2.svg'}`} className={classes.screenshot} ></img>
                </Typography>
            </Box>
          </Grid>
        </div>
        <div>
        <Grid item xs={12}>
            <Box>
                <Typography>
                    <img src={`${process.env.PUBLIC_URL + '/assets/Carousel_3.svg'}`} className={classes.screenshot} ></img>
                </Typography>
            </Box>
          </Grid>
        </div>
      </Slider>
    )};
}
