  {"name": "Carousel_1", 
   "header": "Find your home.", 
   "detail": 
            `Discover family and mentors in 
            areas where you thought you were alone. 
            Discover individuals who share your 
            struggles or your quirkiest combination of interests.
            `
  },

  {"name": "Carousel_2", 
   "header": "Expand your horizons.", 
   "detail": 
            `
            Encounter people and stories 
            that are vastly different from 
            you but still somehow similar. 
            Learn about new areas and how 
            they connect with your existing passions.
            `
  },

  {"name": "Carousel_3", 
   "header": "Coalition Building.", 
   "detail": 
            `
            Easily find, communicate, 
            and organize with others who are 
            working towards similar causes.

            `
  },
]



export default class Carousel extends React.Component {

  state = {
    hover: false,
    };

    toggleHover = () => {
      this.setState({
          hover: !this.state.hover
      })
  }

  classes = makeStyles((theme) => ({
    header: {
      fontFamily: 'Poppins',
      fontSize: '1.5rem',
      color: 'black',
      fontWeight: 'bold',
      lineHeight: '99%', 
      padding: '2px',
    },
  }))


  render () {
  //const classes = useStyles();  
    if (this.state.hover) {


    } else {

    }

    return (
      <Slider 
        dots
        slidesToShow={1}
        autoplay={true}
        autoplaySpeed={3000}
        autoplayScroll={1}

      >
      {slides.map (slide => {
          return (
            <div>
                <Grid container item 
                  style={{}}
                  onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover}

                >
                  <Grid item xs={12}>
                    <Box>
                        <Typography className={this.classes.header}>
                            {slide.header}
                        </Typography>
                    </Box>
                  </Grid>

                  <Grid item xs={12}>
                    <Box>
                        <Typography>
                            <img src={`${process.env.PUBLIC_URL + '/assets/' + slide.name + '.svg'}`} ></img>
                        </Typography>
                    </Box>
                  </Grid>

                </Grid>
            </div>
          )})}

      </Slider>
    )};
}
